import { useQuery } from "react-query";
import axios from "axios";

const fetchStudent = (id) => {
  return axios.get(`http://localhost:4000/students/${id}`);
};

export const useStudentDetailData = (studentId) => {
  return useQuery(["student", studentId], fetchStudent, {
    select: (data) => `${data?.data.name} - ${data?.data.alterEgo}` 
  });
};
