import { toast } from "react-toastify";
import {useStudentsData} from './../hooks/useStudentsData'
import {useFriendsData} from './../hooks/useFriendsData'

export default function ParallelQueriesPage() {
  const onSuccess = (data) => {
    const name = data?.request.responseURL.includes('students') ? 'students' : 'friends'
    toast.success(`called data ${name} success`, {onClose: 1500})
  }

  const onError = (error) => {
    const name = error?.request.responseURL.includes('students') ? 'students' : 'friends'
    toast.error(`${name} - ${error.message}`)
  }

  const {data: students} = useStudentsData(onSuccess, onError)
  const {data: friends} = useFriendsData(onSuccess, onError)

  return <h4>Parallel Queries Page</h4>;
}
