import {useQuery} from 'react-query'
import axios from 'axios'
import { toast } from 'react-toastify';

function RQStudentsPage(){
    const {isLoading, isError, data, error} = useQuery('students', () => {
        return  axios.get("http://localhost:4000/students")
    })

    if(isLoading) return <div>Loading...</div>
    if(isError) toast.error(error.message)

    return (
        <>
        <div>RQ Students Page</div>
        {data.map(student => <div key={student.id}>{student.alterEgo}</div>)}
        </>
    )
}

export default RQStudentsPage
